export const logout = () => {
  return {
    type: LOGOUT
  };
};

export const login = (mobile) => {

  let uri = `${baseUrl}/login/otp`;

  return dispatch => {
    dispatch(() => ({
      type: LOGIN
    })
    )

    fetch(uri, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        mobile
      })
    })
      .then(res => res.json())
      .then(res => {
        if (res.message) {
          dispatch(() => ({
            type: SET_USERNAME, username: mobile, message: res.message
          }))
        }
        else if (res.errors && res.errors.mobile.length !== 0)
          dispatch(() => ({
            type: SET_USERNAME_ERROR, error: res.errors.mobile[0]
          }))
      })
      .catch(err => {
        dispatch(() => ({
          type: SET_USERNAME_ERROR, err
        }));
      });
  };
};

//*******************************************

