export const logout = () => {
  return {
    type: LOGOUT
  };
};

const loginReq = () => ({
  type: LOGIN
})

const setUsername = (mobile, message) => ({
  type: SET_USERNAME,
  username: mobile,
  message
})

const setUsernameError = (error) => ({
  type: SET_USERNAME_ERROR,
  error
})

export const login = (mobile) => {

  let uri = `${baseUrl}/login/otp`;

  return dispatch => {
    dispatch(loginReq())

    fetch(uri, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        mobile
      })
    })
      // .then(res => res.json())
      .then(res => {
        res.json().then(res => {
          console.log("here", res)
          if (res.message) {
            dispatch(setUsername(mobile, res.message))
          }
          else if (res.errors && res.errors.mobile.length !== 0)
            dispatch(setUsernameError(res.errors.mobile[0]))
        })
      })
      .catch(err => {
        dispatch(setUsernameError(err));
      });
  };
};

//*******************************************

export const setToken = (otp) => {
  const mobile = getState.username

  let uri = `${baseUrl}/login?mobile=${mobile}&code=${otp}`;

  return (dispatch, getState) => {
    dispatch(() => ({
      type: SET_TOKEN
    })
    )

    fetch(uri, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        mobile
      })
    })
      .then(res => res.json())
      .then(res => {
        if (res.access_token && res.access_token !== "") {
          dispatch(() => ({
            type: SET_TOKEN_COMPLETE, token: result.access_token
          }))
          dispatch(() => ({
            type: LOGIN_COMPLETE
          }))
        }
        else if (res.errors && res.errors.mobile.length !== 0) {
          dispatch(() => ({
            type: SET_TOKEN_ERROR, error: res.errors.mobile[0]
          }))
          dispatch(() => ({
            type: LOGIN_ERROR
          }))
        }

      })
      .catch(err => {
        dispatch(() => ({
          type: SET_TOKEN_ERROR, err
        }));
      });
  };
};
